import React from 'react'

import Script from 'react-load-script';
/*global google*/

class Search extends React.Component {
    constructor(props) {
      super(props);

      this.state = {
        city: '',
        query: ''
      };

      this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleScriptLoad = () => {
      const options = {
        types: ['(cities)'],
      };

       this.autocomplete = new google.maps.places.Autocomplete(
        document.getElementById('autocomplete'),
        options,
      );


      this.autocomplete.setFields(['address_components', 'formatted_address']);


      this.autocomplete.addListener('place_changed', this.handlePlaceSelect);
    }

    handlePlaceSelect = () => {


      const addressObject = this.autocomplete.getPlace();
      const address = addressObject.address_components;


      if (address) {

        this.setState(
          {
            city: address[0].long_name,
            query: addressObject.formatted_address,
          }
        );
      }
    }

    handleSubmit(address){
      console.log(address.latlng)
    }

    render() {
        return (
          <div>
            <Script
              url="https://maps.googleapis.com/maps/api/js?key=AIzaSyCnqBcICpcIUF4OcyIjqIEoIMXYOXqZnG8&libraries=places"
              onLoad={this.handleScriptLoad}
            />
            <div className = 'search-box'>
            <form onSubmit = {this.handleSubmit}>
              <input className = 'search-text'type = 'textbox' id="autocomplete" placeholder="Type your city"  
              />
            </form>

            </div>
          </div>
        );
      }
}

export default Search
