import React from 'react'

import Script, {
  geocodeByAddress,
  getLatLng,
} from 'react-load-script';
/*global google*/

class Search extends React.Component {
    constructor(props) {
      super(props);

      this.state = {
        city: '',
        query: ''
      };

      this.handlePlaceSelect = this.handlePlaceSelect.bind(this)

    }

    handleScriptLoad = () => {
      const options = {
        types: ['(cities)'],
      };

       this.autocomplete = new google.maps.places.Autocomplete(
        document.getElementById('autocomplete'),
        options,
      );
      this.autocomplete.setFields(['geometry'])

      this.autocomplete.addListener('place_changed', this.handlePlaceSelect);
    }

    handlePlaceSelect = () => {


      const addressObject = this.autocomplete.getPlace();
      const address = addressObject.address_components;
      console.log(addressObject.geometry)

      if (address) {

        this.setState(
          {
            city: address[0].long_name,
            query: addressObject.formatted_address,
          }
        );
      }
    }


    render() {
        return (
          <div>
            <Script
              url="https://maps.googleapis.com/maps/api/js?key=AIzaSyCnqBcICpcIUF4OcyIjqIEoIMXYOXqZnG8&libraries=places"
              onLoad={this.handleScriptLoad}
            />
            <div className = 'search-box'>
            <form onSubmit = {this.handlePlaceSelect}>
              <input className = 'search-text'type = 'textbox' id="autocomplete" placeholder="Type your city"  
              />
              <button> submit</button>
            </form>

            </div>
          </div>
        );
      }
}

export default Search
