import React from 'react'
import SearchBar from './SearchBar'
import WeatherDisplay from './WeatherDisplay'

class Display extends React.Component{
    constructor () {
        super()
        this.state = {
            temperature : '10',
            humidity: '0.65',
            precipType: 'rain',
            precipProbability: '0.9',
            wind: '5.59',
            summary: 'bad',
            lat: '10',
            lng: '11',

        }
    }


    update = ({lat,lng}) => {
        this.setState({
            lat,lng
        })
    }

    componentDidUpdate(prevProps) {
        if (prevProps.lat != this.props.data) {
            console.log(prevProps)    

            const lat = this.state.lat;
            const lng = this.state.lng;

        fetch (`https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/db3b0ba27138ac7fdc854f5529ba7e78/${lat},${lng}`)
        .then (results => results.json())
        .then (response => {
            this.setState ({
                temperature: response.currently.temperature,
                humidity: response.currently.humidity,
                wind: response.currently.windSpeed,
                icon: response.currently.icon,
                precipType: response.currently.precipType,  
                precipProbability: response.currently.precipProbability,
                summary: response.currently.summary,

            })
            console.log(response)
            console.log(lat,lng)

        })
    } else {console.log('error')}
    } 

    render () {
        return (
            <div>
                <SearchBar 
                update = {this.update} 
                />
                <WeatherDisplay 
                    data = {this.state}
                />
                 <p> {this.state.lat} </p>
            </div>

        )
    }
}

export default Display
