import React from 'react'

import Script from 'react-load-script';
/*global google*/

class SearchBar extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            city: '',
            query: ''
        }
    }
    handleScriptLoad = () => {
        const options = {
            types: ['(cities)'],
        }
        this.autocomplete = new google.maps.places.Autocomplete(
            document.getElementById('autocomplete'), options
        );

        this.autocomplete.addListener('place_changed', this.handlePlaceSelect);
    };

    handlePlaceSelect = () => {
        const addressObject = this.autocomplete.getPlace();
        const address = addressObject.address_components; 
        if (address) {
            this.setState(
                {
                    city: address[0].long_name,
                    query: addressObject.formatted_address,
                }
            )
        }
    }



    render() {
        return (
            <div>
                <script type="text/javascript" src="https://maps.googleapis.com/maps/api/js?key=AIzaSyCnqBcICpcIUF4OcyIjqIEoIMXYOXqZnG8&libraries=places"
                onLoad = {this.handleScriptLoad}></script>
                <div className = 'search-box'> 

                    <input 
                        className = 'search-text' 
                        type = 'text' 
                        name = 'location' 
                        placeholder = 'Type your location'>
                        value = {this.state.query}

                    </input>
                    <button> <img draggable = 'false' src = 'https://cdn2.iconfinder.com/data/icons/media-and-navigation-buttons-round/512/Button_15-512.png' 
                    alt = 'search button' /></button>

                </div>
            </div>
        )
    }
}

export default SearchBar
